import moment from '../moment';

export default moment.defineLocale('ko', {
    months: '1_2_3_4_5_6_7_8_9_10_11_12'.split('_'),
    monthsShort: '1_2_3_4_5_6_7_8_9_10_11_12'.split(
        '_'
    ),
    weekdays: '______'.split('_'),
    weekdaysShort: '______'.split('_'),
    weekdaysMin: '______'.split('_'),
    longDateFormat: {
        LT: 'A h:mm',
        LTS: 'A h:mm:ss',
        L: 'YYYY.MM.DD.',
        LL: 'YYYY MMMM D',
        LLL: 'YYYY MMMM D A h:mm',
        LLLL: 'YYYY MMMM D dddd A h:mm',
        l: 'YYYY.MM.DD.',
        ll: 'YYYY MMMM D',
        lll: 'YYYY MMMM D A h:mm',
        llll: 'YYYY MMMM D dddd A h:mm',
    },
    calendar: {
        sameDay: ' LT',
        nextDay: ' LT',
        nextWeek: 'dddd LT',
        lastDay: ' LT',
        lastWeek: ' dddd LT',
        sameElse: 'L',
    },
    relativeTime: {
        future: '%s ',
        past: '%s ',
        s: ' ',
        ss: '%d',
        m: '1',
        mm: '%d',
        h: ' ',
        hh: '%d',
        d: '',
        dd: '%d',
        M: ' ',
        MM: '%d',
        y: ' ',
        yy: '%d',
    },
    dayOfMonthOrdinalParse: /\d{1,2}(||)/,
    ordinal: function (number, period) {
        switch (period) {
            case 'd':
            case 'D':
            case 'DDD':
                return number + '';
            case 'M':
                return number + '';
            case 'w':
            case 'W':
                return number + '';
            default:
                return number;
        }
    },
    meridiemParse: /|/,
    isPM: function (token) {
        return token === '';
    },
    meridiem: function (hour, minute, isUpper) {
        return hour < 12 ? '' : '';
    },
});
