//! moment.js locale configuration
//! locale : Mongolian [mn]
//! author : Javkhlantugs Nyamdorj : https://github.com/javkhaanj7

import moment from '../moment';

function translate(number, withoutSuffix, key, isFuture) {
    switch (key) {
        case 's':
            return withoutSuffix ? ' ' : ' ';
        case 'ss':
            return number + (withoutSuffix ? ' ' : ' ');
        case 'm':
        case 'mm':
            return number + (withoutSuffix ? ' ' : ' ');
        case 'h':
        case 'hh':
            return number + (withoutSuffix ? ' ' : ' ');
        case 'd':
        case 'dd':
            return number + (withoutSuffix ? ' ' : ' ');
        case 'M':
        case 'MM':
            return number + (withoutSuffix ? ' ' : ' ');
        case 'y':
        case 'yy':
            return number + (withoutSuffix ? ' ' : ' ');
        default:
            return number;
    }
}

export default moment.defineLocale('mn', {
    months: ' _ _ _ _ _ _ _ _ _ _  _  '.split(
        '_'
    ),
    monthsShort:
        '1 _2 _3 _4 _5 _6 _7 _8 _9 _10 _11 _12 '.split(
            '_'
        ),
    monthsParseExact: true,
    weekdays: '______'.split('_'),
    weekdaysShort: '______'.split('_'),
    weekdaysMin: '______'.split('_'),
    weekdaysParseExact: true,
    longDateFormat: {
        LT: 'HH:mm',
        LTS: 'HH:mm:ss',
        L: 'YYYY-MM-DD',
        LL: 'YYYY  MMMM D',
        LLL: 'YYYY  MMMM D HH:mm',
        LLLL: 'dddd, YYYY  MMMM D HH:mm',
    },
    meridiemParse: /|/i,
    isPM: function (input) {
        return input === '';
    },
    meridiem: function (hour, minute, isLower) {
        if (hour < 12) {
            return '';
        } else {
            return '';
        }
    },
    calendar: {
        sameDay: '[] LT',
        nextDay: '[] LT',
        nextWeek: '[] dddd LT',
        lastDay: '[] LT',
        lastWeek: '[] dddd LT',
        sameElse: 'L',
    },
    relativeTime: {
        future: '%s ',
        past: '%s ',
        s: translate,
        ss: translate,
        m: translate,
        mm: translate,
        h: translate,
        hh: translate,
        d: translate,
        dd: translate,
        M: translate,
        MM: translate,
        y: translate,
        yy: translate,
    },
    dayOfMonthOrdinalParse: /\d{1,2} /,
    ordinal: function (number, period) {
        switch (period) {
            case 'd':
            case 'D':
            case 'DDD':
                return number + ' ';
            default:
                return number;
        }
    },
});
