//! moment.js locale configuration
//! locale : Sinhalese [si]
//! author : Sampath Sitinamaluwa : https://github.com/sampathsris

import moment from '../moment';

/*jshint -W100*/
export default moment.defineLocale('si', {
    months: '___________'.split(
        '_'
    ),
    monthsShort: '___________'.split(
        '_'
    ),
    weekdays:
        '______'.split(
            '_'
        ),
    weekdaysShort: '______'.split('_'),
    weekdaysMin: '______'.split('_'),
    weekdaysParseExact: true,
    longDateFormat: {
        LT: 'a h:mm',
        LTS: 'a h:mm:ss',
        L: 'YYYY/MM/DD',
        LL: 'YYYY MMMM D',
        LLL: 'YYYY MMMM D, a h:mm',
        LLLL: 'YYYY MMMM D [] dddd, a h:mm:ss',
    },
    calendar: {
        sameDay: '[] LT[]',
        nextDay: '[] LT[]',
        nextWeek: 'dddd LT[]',
        lastDay: '[] LT[]',
        lastWeek: '[] dddd LT[]',
        sameElse: 'L',
    },
    relativeTime: {
        future: '%s',
        past: '%s ',
        s: ' ',
        ss: ' %d',
        m: '',
        mm: ' %d',
        h: '',
        hh: ' %d',
        d: '',
        dd: ' %d',
        M: '',
        MM: ' %d',
        y: '',
        yy: ' %d',
    },
    dayOfMonthOrdinalParse: /\d{1,2} /,
    ordinal: function (number) {
        return number + ' ';
    },
    meridiemParse: / | |.|../,
    isPM: function (input) {
        return input === '..' || input === ' ';
    },
    meridiem: function (hours, minutes, isLower) {
        if (hours > 11) {
            return isLower ? '..' : ' ';
        } else {
            return isLower ? '..' : ' ';
        }
    },
});
