import moment from '../moment';

export default moment.defineLocale('zh-hk', {
    months: '___________'.split(
        '_'
    ),
    monthsShort: '1_2_3_4_5_6_7_8_9_10_11_12'.split(
        '_'
    ),
    weekdays: '______'.split('_'),
    weekdaysShort: '______'.split('_'),
    weekdaysMin: '______'.split('_'),
    longDateFormat: {
        LT: 'HH:mm',
        LTS: 'HH:mm:ss',
        L: 'YYYY/MM/DD',
        LL: 'YYYYMD',
        LLL: 'YYYYMD HH:mm',
        LLLL: 'YYYYMDdddd HH:mm',
        l: 'YYYY/M/D',
        ll: 'YYYYMD',
        lll: 'YYYYMD HH:mm',
        llll: 'YYYYMDdddd HH:mm',
    },
    meridiemParse: /|||||/,
    meridiemHour: function (hour, meridiem) {
        if (hour === 12) {
            hour = 0;
        }
        if (meridiem === '' || meridiem === '' || meridiem === '') {
            return hour;
        } else if (meridiem === '') {
            return hour >= 11 ? hour : hour + 12;
        } else if (meridiem === '' || meridiem === '') {
            return hour + 12;
        }
    },
    meridiem: function (hour, minute, isLower) {
        var hm = hour * 100 + minute;
        if (hm < 600) {
            return '';
        } else if (hm < 900) {
            return '';
        } else if (hm < 1200) {
            return '';
        } else if (hm === 1200) {
            return '';
        } else if (hm < 1800) {
            return '';
        } else {
            return '';
        }
    },
    calendar: {
        sameDay: '[]LT',
        nextDay: '[]LT',
        nextWeek: '[]ddddLT',
        lastDay: '[]LT',
        lastWeek: '[]ddddLT',
        sameElse: 'L',
    },
    dayOfMonthOrdinalParse: /\d{1,2}(||)/,
    ordinal: function (number, period) {
        switch (period) {
            case 'd':
            case 'D':
            case 'DDD':
                return number + '';
            case 'M':
                return number + '';
            case 'w':
            case 'W':
                return number + '';
            default:
                return number;
        }
    },
    relativeTime: {
        future: '%s',
        past: '%s',
        s: '',
        ss: '%d ',
        m: '1 ',
        mm: '%d ',
        h: '1 ',
        hh: '%d ',
        d: '1 ',
        dd: '%d ',
        M: '1 ',
        MM: '%d ',
        y: '1 ',
        yy: '%d ',
    },
});
